import { StudioBaseProps } from '../StudioBase';
import { StudioButtonVariant } from '../StudioButton';
export interface StudioLayoutButtonConfig extends Omit<StudioBaseProps<HTMLButtonElement>, 'onClick' | 'label' | 'size' | 'type' | 'content'> {
    /**
     * Type of the button.
     * @examples
     * type: "button"
     */
    type: 'button';
    /**
     * Icon to be displayed in the button.
     * @examples
     * icon: "close"
     */
    icon?: string;
    /**
     * Label for the button, can be a ReactNode or a function returning a ReactNode.
     * @examples
     * label: "Button"
     */
    label?: string;
    /**
     * Size of the button.
     * @examples
     * size: "large"
     */
    size?: string;
    /**
     * Variant of the button style.
     * @examples
     * variant: "primary"
     */
    variant?: `${StudioButtonVariant}`;
    /**
     * Tooltip text for the button.
     * @examples
     * tooltip: "Click to submit"
     */
    tooltip?: string;
    /**
     * HTML button type attribute.
     * @examples
     * buttonType: "submit"
     */
    buttonType?: HTMLButtonElement['type'];
    /**
     * Indicates if the button is active.
     * @examples
     * active: true
     */
    active?: boolean;
    /**
     * Events to be handled by the editor.
     * @examples
     * editorEvents: {
     *   'component:add': ({ editor }) => { console.log('Component added'); }
     * }
     */
    editorEvents?: object;
    /**
     * Click event handler for the button.
     * @examples
     * onClick: ({ event }) => { alert('Button clicked!'); }
     */
    onClick?: '__fn__';
}
